package romanovskaya.lesson8;public class GenericsArray<T> {    T arr[];    public GenericsArray(T[] arr){    this.arr = arr;    }    public T getByIndex(int index){        return arr[index];    }    public T[] getArr(){        return arr;    }    public void setArr(T[] arr){        this.arr = arr;    }    void print(){        for ( T t : arr){            System.out.print(t);        }    }}